changeSet.sql:
CREATE OR ALTER PROCEDURE get_employee_by_id
    @empId INT
AS
BEGIN
    SELECT id, name, email, phone, company_name,
           'ACTIVE' AS status
    FROM employee
    WHERE id = @empId;
END;

changeSet.rollback:
CREATE OR ALTER PROCEDURE get_employee_by_id
    @empId INT
AS
BEGIN
    SELECT id, name, email, phone, company_name
    FROM employee
    WHERE id = @empId;
END;
